    public static void writeToFile(String[] personList) {
        try {
            FileWriter myWriter = new FileWriter("person-names.txt");
            for (String person : personList) {
                myWriter.write(person);
                myWriter.write("\n");
            }
            myWriter.close();
        } catch (IOException e) {
            System.out.println("An error occurred");
            e.printStackTrace();
        }
    }

